scripts.afterResponse:
let response = insomnia.response.json();

if (response.token) {
    // Define o token como variável de ambiente
    insomnia.environment.set("token", response.token);
    console.log("Token salvo como variável de ambiente.");
} else if (response.user && response.user.token) {
    // Caso o token esteja dentro de user.token
    insomnia.environment.set("token", response.user.token);
    console.log("Token salvo como variável de ambiente a partir de user.token.");
} else {
    console.warn("Token não encontrado na resposta.");
}

let response = insomnia.response.json();
if(!response) console.warn("service nao encontrado")
if (response[0]) {
    insomnia.environment.set("serviceId", response[0].id);
    console.log("serviceId salvo com sucesso!");
} else {
    console.warn("Servico nao encontrado");
}

let response = insomnia.response.json();
if(!response) console.warn("products nao encontrado")
if (response[0]) {
    insomnia.environment.set("productId", response[0].id);
    console.log("productid salvo com sucesso!");
} else {
    console.warn("Produto nao encontrado");
}

let response = insomnia.response.json();
if(response && response.id){
  insomnia.environment.set("categoryId", response.id);
  console.log("categoryId salvo com sucesso!");
} else {
  console.warn("Categoria nao cadastrada");
}

let response = insomnia.response.json();
if(!response.users) console.warn("users nao encontrado")
const barber = response.users.find(user=>user.name === "Barber")
const client = response.users.find(user=>user.name === "Client")

if (client) {
    insomnia.environment.set("clientId", client.id);
    console.log("userid salvo com sucesso!");
} else {
    console.warn("Cliente nao encontrado");
}

if (barber) {
    insomnia.environment.set("barberId", barber.id);
    console.log("userid salvo com sucesso!");
} else {
    console.warn("Barbeiro nao encontrado");
}
